contents.value:
%{let classper = 0;
    entity.items.forEach(item => {
       if(item.system.props.classper) {
           if(item.system.props.duplication) {
               classper += Number(item.system.props.classper) * 2;
           } else {
               classper += Number(item.system.props.classper);
           }
       }
    });
    const perinfut = entity.system.props.perinfut ? Number(entity.system.props.perinfut) : 0;
    classper += + perinfut;

    return classper;}%

%{
let classAtk = 0;
entity.items.forEach(item => {
    if(item.system.props.classatk) {
        classAtk += Number(item.system.props.classatk);
    }
});

let guardianAtk = 0;
entity.items.forEach(item => {
    if(item.system.props.isEquipped && item.system.props.guardianatk) {
        guardianAtk += Number(item.system.props.guardianatk);
    }
});

let optionAtk = 0;
entity.items.forEach(item => {
    if(item.system.props.isEquipped && item.system.props.optionatk) {
        optionAtk += Number(item.system.props.optionatk);
    }
});

const atkBonus = entity.system.props.atkbonus ? Number(entity.system.props.atkbonus) : 0;
const atkInfut = entity.system.props.atkinfut ? Number(entity.system.props.atkinfut) : 0;

const totalAtk = classAtk + guardianAtk + optionAtk + atkBonus + atkInfut;

return totalAtk;
}%

