contents.value:
%{
    const reflec = Number(${reflecvalue}$) || 0;
    const per = Number(${pervalue}$) || 0;
    const baseHit = Math.floor((reflec + per) / 2);

    let classHit = 0;
    entity.items.forEach(item => {
        if(item.system.props.classhit) {
            classHit += Number(item.system.props.classhit);
        }
    });

    let guardianHit = 0;
    entity.items.forEach(item => {
        if(item.system.props.isEquipped && item.system.props.guardianhit) {
            guardianHit += Number(item.system.props.guardianhit);
        }
    });

    let weaponHit = 0;
    entity.items.forEach(item => {
        if(item.system.props.isEquipped && item.system.props.weaponhit) {
            weaponHit += Number(item.system.props.weaponhit);
        }
    });

    let optionHit = 0;
    entity.items.forEach(item => {
        if(item.system.props.isEquipped && item.system.props.optionhit) {
            optionHit += Number(item.system.props.optionhit);
        }
    });

    const hitInfut = entity.system.props.hitinfut ? Number(entity.system.props.hitinfut) : 0;

    const totalHit = baseHit + classHit + guardianHit + weaponHit + optionHit + hitInfut;

    return totalHit;
    }%

%{
    const reflec = Number(${reflecvalue}$) || 0;
    const luck = Number(${luckvalue}$) || 0;
    const baseEvasion = Math.floor((reflec + luck) / 2);

