    entity.items.forEach(item => {
        if(item.system.props.classevasion) {
            classEvasion += Number(item.system.props.classevasion);
        }
    });

    let guardianEvasion = 0;
    entity.items.forEach(item => {
        if(item.system.props.isEquipped && item.system.props.guardianevasion) {
            guardianEvasion += Number(item.system.props.guardianevasion);
        }
    });

    let weaponEvasion = 0;
    entity.items.forEach(item => {
        if(item.system.props.isEquipped && item.system.props.weaponevasion) {
            weaponEvasion += Number(item.system.props.weaponevasion);
        }
    });

    let optionEvasion = 0;
    entity.items.forEach(item => {
        if(item.system.props.isEquipped && item.system.props.optionevasion) {
            optionEvasion += Number(item.system.props.optionevasion);
        }
    });

    const evasionInfut = entity.system.props.evasioninfut ? Number(entity.system.props.evasioninfut) : 0;

    const totalEvasion = baseEvasion + classEvasion + guardianEvasion + weaponEvasion + optionEvasion + evasionInfut;

    return totalEvasion;
