            <i class="fas fa-stopwatch" style="color: #4a4a4a;"></i>
            <h3 style="
                margin: 0;
                font-size: var(--font-size-14);
                color: #4a4a4a;">
                순서
            </h3>
        </div>
    </div>

    <div style="
        background: white;
        border-radius: 8px;
        padding: 12px;
        margin-top: 6px;
        box-shadow: 0 2px 5px rgba(0,0,0,0.05);">

        <div style="
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 12px;">

            <div style="
                display: flex;
                align-items: center;
                gap: 8px;">
                <span style="
                    font-size: var(--font-size-14);
                    color: #4a4a4a;
                    font-weight: bold;">
                    결과
                </span>
                <span style="
                    background: #4a4a4a;
                    color: white;
                    padding: 4px 12px;
                    border-radius: 6px;
                    font-size: var(--font-size-16);
                    font-weight: bold;
                    min-width: 40px;
                    text-align: center;">
                    ${initValue}
                </span>
            </div>

            <div style="
                display: flex;
                align-items: center;
                gap: 4px;">
                ${Array.from({ length: Math.min(initValue, 10) }, (_, i) => `
                    <div style="
                        width: 8px;
                        height: ${8 + (i * 2)}px;
                        background: ${i < initValue ? '#4a4a4a' : '#e9ecef'};
                        border-radius: 2px;">
                    </div>
                `).join('')}
            </div>
        </div>

        <div style="
            margin-top: 8px;
            text-align: center;
            font-size: var(--font-size-12);
            color: #666;">
            ${getInitiativeDescription(initValue)}
        </div>
    </div>
</div>
`;

// 순서값에 따른 설명 반환 함수
function getInitiativeDescription(init) {
    if (init >= 8) return "최우선 행동";
    if (init >= 6) return "빠른 행동";
    if (init >= 4) return "일반 행동";
    if (init >= 2) return "느린 행동";
    return "매우 느린 행동";
}

ChatMessage.create({
    content: content,
    speaker: ChatMessage.getSpeaker()
});}%

%{const fpvalue = `${fpvalue}$`;
const hpvalue = `${hpvalue}$`;
const will = `${willbase}$`;

// List of effects to remove if present
const effectsToRemove = ["낭패", "마비", "방심", "실속", "중압", "증오", "침식", "파워다운", "포박"];

(async () => {
    try {
        // 먼저 토큰을 가져옵니다.
        let token = canvas.tokens.controlled[0];
        if (!token) {
            const tokens = canvas.tokens.placeables.filter(t => t.actor);
            token = tokens[0];
        }

        const actor = token?.actor;
        if (!actor) {
            ui.notifications.warn("시트에서 사용할 캐릭터 또는 선택된 토큰이 없습니다.");
            return;
        }

        // will 수치만큼 회복
        if (fpvalue == 0) {
            await actor.update({ "system.props.fpvalue": will });
        } else if (hpvalue == 0) {
            await actor.update({ "system.props.hpvalue": will });
        }

        // Remove specified effects if they are active on the token
        for (let effectName of effectsToRemove) {
            const effect = token.actor.effects.find(e => e.label === effectName);
            if (effect) {
                await effect.delete();
                ui.notifications.info(`${effectName} 상태가 제거되었습니다.`);
            }
        }

        // Apply "브레이크" effect and play Sequencer animations
        if (token) {
            await applyEffect(token, "브레이크");

            // 시퀸서 효과 재생
            new Sequence()
                .effect()
                .file("jb2a.impact.011.orange")
                .atLocation(token)
                .play();

            new Sequence()
                .effect()
                .file("jb2a.impact.ground_crack.orange.01")
                .atLocation(token)
                .belowTokens() // 토큰 아래에 그라운드 효과 생성
                .play();

            new Sequence()
                .effect()
                .file("jb2a.static_electricity.03.orange")
                .atLocation(token)
                .scale(0.5)
                .play();
        } else {
            ui.notifications.warn("토큰을 찾을 수 없습니다.");
        }
    } catch (error) {
        console.error("브레이크 상태 적용 중 오류 발생:", error);
        ui.notifications.error("브레이크 상태 적용 중 오류가 발생했습니다.");
    }
})();

async function applyEffect(token, effectName) {
    if (effectName.toLowerCase().trim() === '없음') {
        return;
    }

    const statusId = effectName.toLowerCase().trim();
    const statusEffect = CONFIG.statusEffects.find(e =>
        e.id === statusId ||
        e.label.toLowerCase() === statusId
    );

    if (!statusEffect) {
        ui.notifications.warn(`${effectName} 상태가 시스템에 등록되어 있지 않습니다.`);
        return;
    }

    try {
        const hasEffect = token.document.hasStatusEffect(statusEffect.id);
        if (!hasEffect) {
            await token.toggleEffect(statusEffect);
            ui.notifications.info(`${effectName} 상태가 적용되었습니다.`);
        } else {
            ui.notifications.info(`${effectName} 상태가 이미 적용되어 있습니다.`);
        }
    } catch (error) {
        ui.notifications.error(`${effectName} 상태 적용 실패: ${error}`);
        console.error(error);
    }
}
}%

