%{async function createCustomTemplate() {
    console.log("매크로 시작");
    const token = canvas.tokens.controlled[0];
    if (!token) {
        ui.notifications.warn("토큰을 선택해주세요.");
        return;
    }

    const weaponRange = `${item.weaponrange}$`;
    const weaponTarget = `${item.weapontarget}$`;
    console.log("입력된 범위:", weaponRange);
    console.log("입력된 대상:", weaponTarget);

    const baseGridSize = canvas.grid.size;
    const gameGridSize = baseGridSize * 2;
    const sceneWidth = canvas.scene.width;
    const sceneHeight = canvas.scene.height;

    // 씬의 시작 좌표 계산
    const sceneStartX = Math.floor((canvas.scene.dimensions.width - sceneWidth) / 2);
    const sceneStartY = Math.floor((canvas.scene.dimensions.height - sceneHeight) / 2);

    console.log("Scene dimensions:", {
        width: sceneWidth,
        height: sceneHeight,
        startX: sceneStartX,
        startY: sceneStartY
    });

    let templateData = [];
    let createdTemplates = [];

    function get2x2Corner(x, y) {
        // 씬의 시작점을 기준으로 상대 좌표 계산
        const relativeX = x - sceneStartX;
        const relativeY = y - sceneStartY;

        // 2x2 그리드로 나누어 위치 계산
        const gridX = Math.floor(relativeX / gameGridSize);
        const gridY = Math.floor(relativeY / gameGridSize);

        // 실제 좌표로 변환 (씬의 시작점 고려)
        const cornerX = (gridX * gameGridSize) + sceneStartX;
        const cornerY = (gridY * gameGridSize) + sceneStartY;

        console.log("Grid calculation:", {
            clickX: x,
            clickY: y,
            relativeX,
            relativeY,
            gridX,
            gridY,
            cornerX,
            cornerY,
            sceneStartX,
            sceneStartY
        });

        return { x: cornerX, y: cornerY };
    }

    function addTemplateRect(dx, dy, startX, startY, direction = 'north', color) {
        let [adjustedDx, adjustedDy] = adjustCoordinates(dx, dy, direction);

        const x = startX + (adjustedDx * gameGridSize);
        const y = startY + (adjustedDy * gameGridSize);

        const templateProperties = {
            t: "rect",
            user: game.user.id,
            x: x,
            y: y,
            width: gameGridSize,
            height: gameGridSize,
            direction: 45,
            distance: 1.4142135623730951,
            fillColor: color,
            fillAlpha: 0.5,
            borderColor: color,
            borderAlpha: 0.8,
            flags: { 
                customTemplate: true,
                displayRuler: false
            }
        };

        templateData.push(templateProperties);
    }

    // 매크로 시작 부분에 추가
    Hooks.on('createMeasuredTemplate', (template) => {
        const t = template.object;
        if (t && t.ruler) {
            t.ruler.visible = false;  // ruler를 직접 숨김
        }
    });

    function adjustCoordinates(dx, dy, direction) {
        switch (direction) {
            case 'east': return [-dy, dx];
            case 'south': return [dx, -dy];
            case 'west': return [dy, dx];
            default: return [dx, dy]; // north
        }
    }

    async function selectDirection() {
        const directions = [
            { value: 'east', label: '동' },
            { value: 'west', label: '서' },
            { value: 'south', label: '남' },
            { value: 'north', label: '북' }
        ];
        const directionContent = directions.map(dir => 
            `<option value="${dir.value}">${dir.label}</option>`
        ).join('');
        return new Promise((resolve) => {
            new Dialog({
                title: "방향 선택",
                content: `<form><div class="form-group"><label>방향:</label><select name="direction">${directionContent}</select></div></form>`,
                buttons: {
                    확인: {
                        label: "확인",
                        callback: (html) => resolve(html.find('[name="direction"]').val())
                    }
                }
            }).render(true);
        });
    }

    async function selectCenter() {
        return new Promise((resolve) => {
            const handler = (event) => {
                const { x, y } = event.data.getLocalPosition(canvas.app.stage);
                const corner = get2x2Corner(x, y);

                // 선택된 위치 로깅
                console.log("Selected position:", x, y);
                console.log("Calculated corner:", corner);

                canvas.app.stage.off('click', handler);
                resolve(corner);
            };
            canvas.app.stage.once('click', handler);
            ui.notifications.info("템플릿을 배치할 2x2 영역을 클릭하세요.");
        });
    }

    async function createTemplate(info, isWeaponRange = false, color) {
        let startX, startY;
        let selectedDirection = 'north';

        // 위치 선택 로직 수정
        if (isWeaponRange) {
            // 사정거리는 항상 토큰 위치 기준
            const corner = get2x2Corner(token.x, token.y);
            startX = corner.x;
            startY = corner.y;
        } else if (info.needsSelection || info.type === 'range' || info.type === 'rangeMinMax' || info.type === 'simpleRange') {
            // 범위나 RS, 범위1 등은 클릭으로 위치 선택
            const corner = await selectCenter();
            startX = corner.x;
            startY = corner.y;
        } else {
            // 나머지는 토큰 위치 기준
            const corner = get2x2Corner(token.x, token.y);
            startX = corner.x;
            startY = corner.y;
        }

        // 방향 선택 로직
        if (!isWeaponRange && (info.type === 'line' || info.type === 'wideLine' || info.type === 'radial')) {
            selectedDirection = await selectDirection();
        }

        switch (info.type) {
            case 'line':
                for (let i = 0; i <= info.value; i++) {
                    addTemplateRect(0, -i, startX, startY, selectedDirection, color);
                }
                break;
            case 'wideLine':
                const halfWidth = Math.floor(info.width / 2);
                for (let w = -halfWidth; w <= halfWidth; w++) {
                    for (let i = 0; i <= info.range; i++) {
                        addTemplateRect(w, -i, startX, startY, selectedDirection, color);
                    }
                }
                break;
            case 'radial':
                addTemplateRect(0, 0, startX, startY, selectedDirection, color);
