    }

    async function selectCenter() {
        return new Promise((resolve) => {
            const handler = (event) => {
                const { x, y } = event.data.getLocalPosition(canvas.app.stage);
                const corner = get2x2Corner(x, y);

                // 선택된 위치 로깅
                console.log("Selected position:", x, y);
                console.log("Calculated corner:", corner);

                canvas.app.stage.off('click', handler);
                resolve(corner);
            };
            canvas.app.stage.once('click', handler);
            ui.notifications.info("템플릿을 배치할 2x2 영역을 클릭하세요.");
        });
    }

    async function createTemplate(info, isWeaponRange = false, color) {
        let startX, startY;
        let selectedDirection = 'north';

        // 위치 선택 로직 수정
        if (isWeaponRange) {
            // 사정거리는 항상 토큰 위치 기준
            const corner = get2x2Corner(token.x, token.y);
            startX = corner.x;
            startY = corner.y;
        } else if (info.needsSelection || info.type === 'range' || info.type === 'rangeMinMax' || info.type === 'simpleRange') {
            // 범위나 RS, 범위1 등은 클릭으로 위치 선택
            const corner = await selectCenter();
            startX = corner.x;
            startY = corner.y;
        } else {
            // 나머지는 토큰 위치 기준
            const corner = get2x2Corner(token.x, token.y);
            startX = corner.x;
            startY = corner.y;
        }

        // 방향 선택 로직
        if (!isWeaponRange && (info.type === 'line' || info.type === 'wideLine' || info.type === 'radial')) {
            selectedDirection = await selectDirection();
        }

        switch (info.type) {
            case 'line':
                for (let i = 0; i <= info.value; i++) {
                    addTemplateRect(0, -i, startX, startY, selectedDirection, color);
                }
                break;
            case 'wideLine':
                const halfWidth = Math.floor(info.width / 2);
                for (let w = -halfWidth; w <= halfWidth; w++) {
                    for (let i = 0; i <= info.range; i++) {
                        addTemplateRect(w, -i, startX, startY, selectedDirection, color);
                    }
                }
                break;
            case 'radial':
                addTemplateRect(0, 0, startX, startY, selectedDirection, color);
                if (info.value >= 1) {
                    addTemplateRect(0, -1, startX, startY, selectedDirection, color);
                }
                for (let i = 2; i <= info.value; i++) {
                    for (let dx = -i + 1; dx <= i - 1; dx++) {
                        addTemplateRect(dx, -i, startX, startY, selectedDirection, color);
                    }
                }
                break;
                case 'simpleRange':
                    if (info.value === 1) {
                        // 범위1은 단일 그리드
                        addTemplateRect(0, 0, startX, startY, 'north', color);
                    } else {
                        // 범위2 이상은 실제 값보다 1 작게 처리
                        const adjustedValue = info.value - 1;
                        for (let dx = -adjustedValue; dx <= adjustedValue; dx++) {
                            for (let dy = -adjustedValue; dy <= adjustedValue; dy++) {
                                const distance = Math.abs(dx) + Math.abs(dy);
                                if (distance <= adjustedValue) {
                                    addTemplateRect(dx, dy, startX, startY, 'north', color);
                                }
                            }
                        }
                    }
                    break;

                case 'range':
                case 'rangeMinMax':
                    const minRange = info.min !== undefined ? info.min : 0;
                    const maxRange = info.max !== undefined ? info.max : info.value;
                    for (let dx = -maxRange; dx <= maxRange; dx++) {
                        for (let dy = -maxRange; dy <= maxRange; dy++) {
                            const distance = Math.abs(dx) + Math.abs(dy);
                            if (distance >= minRange && distance <= maxRange) {
                                addTemplateRect(dx, dy, startX, startY, 'north', color);
                            }
                        }
                    }
                    break;
        }
    }

    function parseTarget(targetString) {
        targetString = targetString.replace(/\(선택\)|\$/g, '');

        // 범위와 RS 패턴 동일하게 처리
        if (targetString.match(/^(범위|RS)\d+$/)) {
            const value = Number(targetString.replace(/[^\d]/g, ''));
            return { type: 'simpleRange', value: value, needsSelection: true };
        }

        // 기존 패턴들
        if (targetString.startsWith('직선') || targetString.startsWith('돌파')) {
            return { type: 'line', value: Number(targetString.replace(/[^\d]/g, '')) };
        } else if (targetString.startsWith('폭')) {
            const match = targetString.match(/폭(\d+)(직선|돌파)(\d+)/);
            return match ? { type: 'wideLine', width: Number(match[1]), range: Number(match[3]) } : null;
        } else if (targetString.startsWith('방사')) {
            return { type: 'radial', value: Number(targetString.replace('방사', '')) };
        }
        return null;
    }

    function parseRange(rangeString) {
        rangeString = rangeString.replace(/\$/g, '');
        if (rangeString.includes('~')) {
            const [min, max] = rangeString.split('~').map(Number);
            return { type: 'rangeMinMax', min, max };
        } else if (rangeString) {
            return { type: 'range', min: 0, max: Number(rangeString) };
        }
        return null;
    }

