%{async function createCustomTemplate() {
    console.log("매크로 시작");
    const token = canvas.tokens.controlled[0];
    if (!token) {
        ui.notifications.warn("토큰을 선택해주세요.");
        return;
    }

    const weaponTarget = `${item.btarget}$`;
    console.log("입력된 대상:", weaponTarget);

    const baseGridSize = canvas.grid.size;
    const gameGridSize = baseGridSize * 2;
    const sceneWidth = canvas.scene.width;
    const sceneHeight = canvas.scene.height;

    // 씬의 시작 좌표 계산
    const sceneStartX = Math.floor((canvas.scene.dimensions.width - sceneWidth) / 2);
    const sceneStartY = Math.floor((canvas.scene.dimensions.height - sceneHeight) / 2);

    console.log("Scene dimensions:", {
        width: sceneWidth,
        height: sceneHeight,
        startX: sceneStartX,
        startY: sceneStartY
    });

    let templateData = [];
    let createdTemplates = [];

    function get2x2Corner(x, y) {
        // 씬의 시작점을 기준으로 상대 좌표 계산
        const relativeX = x - sceneStartX;
        const relativeY = y - sceneStartY;

        // 2x2 그리드로 나누어 위치 계산
        const gridX = Math.floor(relativeX / gameGridSize);
        const gridY = Math.floor(relativeY / gameGridSize);

        // 실제 좌표로 변환 (씬의 시작점 고려)
        const cornerX = (gridX * gameGridSize) + sceneStartX;
        const cornerY = (gridY * gameGridSize) + sceneStartY;

        console.log("Grid calculation:", {
            clickX: x,
            clickY: y,
            relativeX,
            relativeY,
            gridX,
            gridY,
            cornerX,
            cornerY,
            sceneStartX,
            sceneStartY
        });

        return { x: cornerX, y: cornerY };
    }

    function addTemplateRect(dx, dy, startX, startY, direction = 'north', color) {
        let [adjustedDx, adjustedDy] = adjustCoordinates(dx, dy, direction);

        const x = startX + (adjustedDx * gameGridSize);
        const y = startY + (adjustedDy * gameGridSize);

        const templateProperties = {
            t: "rect",
            user: game.user.id,
            x: x,
            y: y,
            width: gameGridSize,
            height: gameGridSize,
            direction: 45,
            distance: 1.4142135623730951,
            fillColor: color,
            fillAlpha: 0.5,
            borderColor: color,
            borderAlpha: 0.8,
            flags: { 
                customTemplate: true,
                displayRuler: false
            }
        };

        templateData.push(templateProperties);
    }

    // 매크로 시작 부분에 추가
    Hooks.on('createMeasuredTemplate', (template) => {
        const t = template.object;
        if (t && t.ruler) {
            t.ruler.visible = false;  // ruler를 직접 숨김
        }
    });

    function adjustCoordinates(dx, dy, direction) {
        switch (direction) {
            case 'east': return [-dy, dx];
            case 'south': return [dx, -dy];
            case 'west': return [dy, dx];
            default: return [dx, dy]; // north
        }
    }

    async function selectDirection() {
        const directions = [
            { value: 'east', label: '동' },
            { value: 'west', label: '서' },
            { value: 'south', label: '남' },
            { value: 'north', label: '북' }
        ];
        const directionContent = directions.map(dir => 
            `<option value="${dir.value}">${dir.label}</option>`
        ).join('');
        return new Promise((resolve) => {
            new Dialog({
                title: "방향 선택",
                content: `<form><div class="form-group"><label>방향:</label><select name="direction">${directionContent}</select></div></form>`,
                buttons: {
                    확인: {
                        label: "확인",
                        callback: (html) => resolve(html.find('[name="direction"]').val())
                    }
                }
            }).render(true);
        });
    }

    async function selectCenter() {
        return new Promise((resolve) => {
            const handler = (event) => {
                const { x, y } = event.data.getLocalPosition(canvas.app.stage);
                const corner = get2x2Corner(x, y);

                // 선택된 위치 로깅
                console.log("Selected position:", x, y);
                console.log("Calculated corner:", corner);

                canvas.app.stage.off('click', handler);
                resolve(corner);
            };
            canvas.app.stage.once('click', handler);
            ui.notifications.info("템플릿을 배치할 2x2 영역을 클릭하세요.");
        });
    }

    async function createTemplate(info, isWeaponRange = false, color) {
        let startX, startY;
        let selectedDirection = 'north';

        // 위치 선택 로직 수정
        if (isWeaponRange) {
            // 사정거리는 항상 토큰 위치 기준
            const corner = get2x2Corner(token.x, token.y);
            startX = corner.x;
            startY = corner.y;
        } else if (info.needsSelection || info.type === 'range' || info.type === 'rangeMinMax' || info.type === 'simpleRange') {
            // 범위나 RS, 범위1 등은 클릭으로 위치 선택
            const corner = await selectCenter();
            startX = corner.x;
            startY = corner.y;
        } else {
            // 나머지는 토큰 위치 기준
            const corner = get2x2Corner(token.x, token.y);
            startX = corner.x;
            startY = corner.y;
        }

        // 방향 선택 로직
        if (!isWeaponRange && (info.type === 'line' || info.type === 'wideLine' || info.type === 'radial')) {
            selectedDirection = await selectDirection();
        }

        switch (info.type) {
            case 'line':
                for (let i = 0; i <= info.value; i++) {
                    addTemplateRect(0, -i, startX, startY, selectedDirection, color);
                }
                break;
            case 'wideLine':
                const halfWidth = Math.floor(info.width / 2);
                for (let w = -halfWidth; w <= halfWidth; w++) {
                    for (let i = 0; i <= info.range; i++) {
                        addTemplateRect(w, -i, startX, startY, selectedDirection, color);
                    }
                }
                break;
            case 'radial':
                addTemplateRect(0, 0, startX, startY, selectedDirection, color);
                if (info.value >= 1) {
                    addTemplateRect(0, -1, startX, startY, selectedDirection, color);
                }
                for (let i = 2; i <= info.value; i++) {
                    for (let dx = -i + 1; dx <= i - 1; dx++) {
                        addTemplateRect(dx, -i, startX, startY, selectedDirection, color);
                    }
                }
                break;
                case 'simpleRange':
                    if (info.value === 1) {
                        // 범위1은 단일 그리드
                        addTemplateRect(0, 0, startX, startY, 'north', color);
                    } else {
                        // 범위2 이상은 실제 값보다 1 작게 처리
                        const adjustedValue = info.value - 1;
                        for (let dx = -adjustedValue; dx <= adjustedValue; dx++) {
                            for (let dy = -adjustedValue; dy <= adjustedValue; dy++) {
                                const distance = Math.abs(dx) + Math.abs(dy);
                                if (distance <= adjustedValue) {
                                    addTemplateRect(dx, dy, startX, startY, 'north', color);
                                }
                            }
                        }
                    }
                    break;

                case 'range':
                case 'rangeMinMax':
                    const minRange = info.min !== undefined ? info.min : 0;
                    const maxRange = info.max !== undefined ? info.max : info.value;
                    for (let dx = -maxRange; dx <= maxRange; dx++) {
                        for (let dy = -maxRange; dy <= maxRange; dy++) {
                            const distance = Math.abs(dx) + Math.abs(dy);
                            if (distance >= minRange && distance <= maxRange) {
                                addTemplateRect(dx, dy, startX, startY, 'north', color);
                            }
                        }
                    }
                    break;
        }
    }

    function parseTarget(targetString) {
        targetString = targetString.replace(/\(선택\)|\$/g, '');

        // 범위와 RS 패턴 동일하게 처리
        if (targetString.match(/^(범위|RS)\d+$/)) {
            const value = Number(targetString.replace(/[^\d]/g, ''));
            return { type: 'simpleRange', value: value, needsSelection: true };
        }

        // 기존 패턴들
        if (targetString.startsWith('직선') || targetString.startsWith('돌파')) {
            return { type: 'line', value: Number(targetString.replace(/[^\d]/g, '')) };
        } else if (targetString.startsWith('폭')) {
            const match = targetString.match(/폭(\d+)(직선|돌파)(\d+)/);
            return match ? { type: 'wideLine', width: Number(match[1]), range: Number(match[3]) } : null;
        } else if (targetString.startsWith('방사')) {
            return { type: 'radial', value: Number(targetString.replace('방사', '')) };
        }
        return null;
    }

    function parseRange(rangeString) {
        rangeString = rangeString.replace(/\$/g, '');
        if (rangeString.includes('~')) {
            const [min, max] = rangeString.split('~').map(Number);
            return { type: 'rangeMinMax', min, max };
        } else if (rangeString) {
            return { type: 'range', min: 0, max: Number(rangeString) };
        }
        return null;
    }

    const targetInfo = parseTarget(weaponTarget);

    // 그 다음 대상 범위 표시
    if (targetInfo) {
        await createTemplate(targetInfo, false, "#FF0000");
    }

    if (!targetInfo) {
        ui.notifications.warn("올바른 범위 또는 대상 형식이 아닙니다.");
        return;
    }

    if (templateData.length > 0) {
        let targetTemplates = await canvas.scene.createEmbeddedDocuments("MeasuredTemplate", templateData);
        createdTemplates = createdTemplates.concat(targetTemplates);
    }

    const deleteTemplates = async () => {
        if (createdTemplates.length > 0) {
            await canvas.scene.deleteEmbeddedDocuments("MeasuredTemplate", createdTemplates.map(t => t.id));
            createdTemplates = [];
            console.log("템플릿 삭제 완료");
        }
    };

    const handleKeyPress = async (event) => {
        if (event.key === "Delete" || event.key === "Backspace") {
            await deleteTemplates();
            // 이벤트 리스너 제거
            document.removeEventListener("keydown", handleKeyPress);
            console.log("키 이벤트 리스너 제거됨");
        }
    };

    // 기존 이벤트 리스너 제거 (혹시 모를 중복 방지)
    document.removeEventListener("keydown", handleKeyPress);
    // 새로운 이벤트 리스너 등록
    document.addEventListener("keydown", handleKeyPress);
    console.log("키 이벤트 리스너 등록됨");

    // 템플릿 생성 시도
    if (templateData.length > 0) {
        let targetTemplates = await canvas.scene.createEmbeddedDocuments("MeasuredTemplate", templateData);
        createdTemplates = createdTemplates.concat(targetTemplates);
        console.log(`${createdTemplates.length}개의 템플릿이 생성됨`);
    }

    // 매크로가 다시 실행될 때를 대비해 cleanup 함수 등록
    Hooks.once('createCustomTemplate', () => {
        document.removeEventListener("keydown", handleKeyPress);
        console.log("cleanup: 키 이벤트 리스너 제거됨");
    });
}

// 매크로 실행 전 이전 인스턴스 cleanup
Hooks.callAll('createCustomTemplate');
createCustomTemplate();}%

