                        return;
                    }

                    const guardianId = selectedGuardian.data('guardianId');
                    // TransformationManager 대신 game.MetalicCombatSystem.TransformationManager 사용
                    await game.modules.get('metalic-combat-system').api.TransformationManager.performTransformation(selectedToken, guardianId);
