                ev.stopPropagation();
                const guardianId = ev.currentTarget.dataset.guardianId;
                const details = html.find(`#details-${guardianId}`);
                details.slideToggle(200);

                const button = $(ev.currentTarget);
                button.text(details.is(':visible') ? '상세 정보 닫기' : '상세 정보 보기');
            });
        }
    }, {
        width: 800,
        height: 800,
        resizable: true,
        classes: ['guardian-transform-dialog']
    }).render(true);
}

guardianTransformation();}%

