%{
// 필요한 아이템 정보들을 변수로 선언
let itemName = `${item.name}$`;
let itemLevel = `${item.slv}$`;
let itemTarget = `${item.starget}$`;
let itemRange = `${item.srange}$`;
let itemCost = `${item.scost}$`;
let itemTiming = `${item.stiming}$`;
let itemEffect = `${item.seffect}$`;
let modifierName = `${item.name}$`;
let itemLimit  = `${item.limit}$`;

// 상태 효과 적용 함수를 먼저 정의
async function applyEffect(token, effectName) {
    // '없음'인 경우 그냥 리턴
    if (effectName.toLowerCase().trim() === '없음') {
        return;
    }

    const statusId = effectName.toLowerCase().trim();
    const statusEffect = CONFIG.statusEffects.find(e => 
        e.id === statusId || 
        e.label.toLowerCase() === statusId
    );

    if (!statusEffect) {
        ui.notifications.warn(`${effectName} 상태가 시스템에 등록되어 있지 않습니다.`);
        return;
    }

    try {
        const hasEffect = token.document.hasStatusEffect(statusEffect.id);
        if (!hasEffect) {
            await token.toggleEffect(statusEffect);
            ui.notifications.info(`${effectName} 상태가 적용되었습니다.`);
        } else {
            ui.notifications.info(`${effectName} 상태가 이미 적용되어 있습니다.`);
        }
    } catch (error) {
        ui.notifications.error(`${effectName} 상태 적용 실패: ${error}`);
        console.error(error);
    }
}

// 비용 처리 함수
async function handleCost() {
    console.log('비용 처리 시작');

    let cost = itemCost.toLowerCase().replace(/\s+/g, '');
    console.log('처리할 비용 문자열:', cost);

    // 모든 비용 패턴 찾기 (탄수 패턴 추가)
    let allCosts = [...cost.matchAll(/(\d+)?(fp|hp|en|탄수)(\d+)?/g)];
    console.log('찾은 모든 비용 패턴:', allCosts);

    // 비용 유형별로 분리하고 계산
    let fpTotal = 0;
    let hpTotal = 0;
    let enTotal = 0;
    let bulletTotal = 0;

    allCosts.forEach(match => {
        const type = match[2];  // fp, hp, en, 탄수 중 하나
        const num1 = match[1] ? parseInt(match[1]) : 0;  // 앞쪽 숫자
        const num2 = match[3] ? parseInt(match[3]) : 0;  // 뒤쪽 숫자
        const value = num1 || num2;  // 둘 중 0이 아닌 값 사용

        switch(type) {
            case 'fp': fpTotal += value; break;
            case 'hp': hpTotal += value; break;
            case 'en': enTotal += value; break;
            case '탄수': bulletTotal += value; break;
        }
    });

    // 무기 아이템 찾기
    let weaponItems = actor.items.filter(i => 
        i.system?.props?.type === "weapon" && 
        i.system?.props?.bullets !== undefined
    );

    console.log('계산된 비용:', { fpTotal, hpTotal, enTotal, bulletTotal });

    // 비용 패턴들을 문자열에서 제거하고 남은 부분에서 효과 추출
    let remainingText = cost;
    allCosts.forEach(match => {
        remainingText = remainingText.replace(match[0], ',');
    });

    // 효과 추출 (쉼표로 분리하고 빈 문자열 제거)
    let otherEffects = remainingText
        .split(',')
        .map(effect => effect.trim())
        .filter(effect => effect !== '');

    console.log('추출된 효과:', otherEffects);

    const item = actor.items.find(i => i.name === itemName);
    const hasToggleableEffects = item?.system.modifiers
        ?.some(m => m.conditionalGroup);

    // 사용횟수 확인
    const hasLimit = itemLimit && itemLimit !== '0' && itemLimit !== '$';
    const currentLimit = hasLimit ? parseInt(itemLimit.replace('$', '')) : 0;

    return new Promise((resolve) => {
        new Dialog({
            title: "비용 소비 확인",
            content: `
                <style>
                    .cost-dialog {
                        background: #f0f0f0;
                        padding: 15px;
                        border-radius: 8px;
                    }
                    .cost-list {
                        background: white;
                        padding: 10px 15px;
                        border-radius: 6px;
                        margin: 10px 0;
                        box-shadow: 0 2px 5px rgba(0,0,0,0.05);
                    }
                    .cost-item {
                        display: flex;
                        align-items: center;
                        gap: 8px;
                        padding: 4px 0;
                    }
                    .cost-label {
                        display: inline-flex;
                        align-items: center;
                        padding: 2px 6px;
                        border-radius: 4px;
                        font-size: 12px;
                        font-weight: bold;
                    }
                    .fp-label { color: #2c7be5; }
                    .hp-label { color: #dc3545; }
                    .en-label { color: #6c757d; }
                    .bullet-label { color: #ffc107; }
                    .effect-label { color: #198754; }
                    .consume-option {
                        background: #f8f9fa;
                        padding: 8px;
                        border-radius: 6px;
                        margin-top: 10px;
                        display: flex;
                        align-items: center;
                        gap: 10px;
                    }
                    .toggle-option {
                        background: #f8f9fa;
                        padding: 8px;
                        border-radius: 6px;
                        margin-top: 10px;
                        display: flex;
                        align-items: center;
                        gap: 10px;
                    }
                    .limit-option {
                        background: #f8f9fa;
                        padding: 8px;
                        border-radius: 6px;
                        margin-top: 10px;
                        display: flex;
                        align-items: center;
                        gap: 10px;
                    }
                    .limit-value {
                        color: #666;
                        font-size: 12px;
                        margin-left: auto;
                    }                        
                </style>
                <div class="cost-dialog">
                    <div class="cost-list">
                        ${fpTotal > 0 ? `
                            <div class="cost-item">
                                <span class="cost-label fp-label">FP</span>
                                <span>${fpTotal}</span>
                            </div>
                        ` : ""}
                        ${hpTotal > 0 ? `
                            <div class="cost-item">
                                <span class="cost-label hp-label">HP</span>
                                <span>${hpTotal}</span>
                            </div>
                        ` : ""}
                        ${enTotal > 0 ? `
                            <div class="cost-item">
                                <span class="cost-label en-label">EN</span>
                                <span>${enTotal}</span>
                            </div>
                        ` : ""}
                        ${bulletTotal > 0 ? `
                            <div class="cost-item">
                                <span class="cost-label bullet-label">탄수</span>
