                                    ui.notifications.warn("남은 사용횟수가 없습니다!");
                                    return resolve(false);
                                }

                                const specialtyItem = actor.items.find(i => i.name === itemName.replace(/\$/g, ''));
                                if (specialtyItem) {
                                    await specialtyItem.update({
                                        "system.props.limit": currentLimit - 1
                                    });
                                    console.log('사용횟수 감소:', currentLimit - 1);
                                }
                            }    

                        if (consumeCost) {
