                                    "system.props.hpvalue": hpValue - hpTotal
                                });
                                console.log('HP 소비:', hpTotal);
                            }

                            // EN 소비
                            if (enTotal > 0) {
                                let enValue = actor.system.props.envalue;
                                await actor.update({ 
                                    "system.props.envalue": enValue - enTotal
                                });
                                console.log('EN 소비:', enTotal);
                            }

                            // 탄수 소비
                            if (bulletTotal > 0) {
                                const specialtyItem = actor.items.find(i => i.name === itemName);
                                if (specialtyItem) {
                                    const currentBullets = specialtyItem.system.props.bullets || 0;
                                    if (currentBullets < bulletTotal) {
                                        ui.notifications.warn("남은 탄약이 부족합니다!");
                                        return resolve(false);
                                    }
                                    await specialtyItem.update({
                                        "system.props.bullets": currentBullets - bulletTotal
                                    });
                                    console.log('탄수 소비:', bulletTotal);
                                }
                            }

                            // 기타 효과 적용
                            for (let effect of otherEffects) {
                                if (effect) {
                                    await applyEffect(selectedToken, effect);
                                }
