const toggleItemEffects = async () => {
    const item = actor.items.find(i => i.name === itemName);
    if (!item) {
        ui.notifications.warn(`${itemName}을(를) 찾을 수 없습니다.`);
        return;
    }

    const modifierGroups = new Set(
        item.system.modifiers
            .map(m => m.conditionalGroup)
            .filter(group => group)
    );

    // 토글할 상태가 없는 경우 조용히 리턴
    if (modifierGroups.size === 0) {
        return;
    }

    let activeGroups = actor.system.activeConditionalModifierGroups || [];
    const allGroupsActive = Array.from(modifierGroups)
        .every(group => activeGroups.includes(group));

    try {
        if (allGroupsActive) {
            activeGroups = activeGroups.filter(group => !modifierGroups.has(group));
            ui.notifications.info(`${itemName}의 효과가 비활성화되었습니다.`);
        } else {
            modifierGroups.forEach(group => {
                if (!activeGroups.includes(group)) {
                    activeGroups.push(group);
                }
            });
            ui.notifications.info(`${itemName}의 효과가 활성화되었습니다.`);
        }
        await actor.update({
            "system.activeConditionalModifierGroups": activeGroups
        });
    } catch (error) {
        console.error("효과 토글 중 오류 발생:", error);
        ui.notifications.error("효과 토글 중 오류가 발생했습니다.");
    }
};

